                                        | ({WIDTH_SID{ARGRANT[2]}}&ARSID2)
                                        | ({WIDTH_SID{ARGRANT[3]}}&ARSID3)
                                        | ({WIDTH_SID{ARGRANT[4]}}&ARSID4)
                                        | ({WIDTH_SID{ARGRANT[5]}}&ARSID5)
                                        | ({WIDTH_SID{ARGRANT[6]}}&ARSID6)
                                        | ({WIDTH_SID{ARGRANT[7]}}&ARSID7)
                                        ;
     reg  [NUM-1:0] argrant_reg;
     //-----------------------------------------------------------
     // The atomic access should consist of a read followed by a write.
     // The atomic access should be a single burst transfer.
     //-----------------------------------------------------------
     // read-address arbiter
     //-----------------------------------------------------------
     localparam STAR_RUN    = 'h0,
                STAR_WAIT   = 'h1,
                STAR_LOCK   = 'h2;
     reg [1:0] stateAR=STAR_RUN;
     always @ (posedge ACLK or negedge ARESETn) begin
           if (ARESETn==1'b0) begin
               locked      <= 1'b0;
               locksid     <=  'h0;
               argrant_reg <= 'h0;
               stateAR     <= STAR_RUN;
           end else begin
               case (stateAR)
               STAR_RUN: begin
                    if (|(ARGRANT&ARLOCK)) begin
                        // note that ARLOCK={M1_ARLOCK[1],M1_ALOCK[1]}
                        locked      <= 1'b1;
                        locksid     <= granted_arsid;
                        argrant_reg <= ARGRANT;
                        stateAR     <= STAR_LOCK;
                    end else begin
                        if (|ARGRANT) begin
                           // prevent the case that
                           // the granted-one is not completed due to ~ARREADY
                           // and new higher-priority-one joined,
                           // then things can go wrong.
                           if (~|(ARGRANT&ARREADY)) begin
                               argrant_reg <= ARGRANT;
                               stateAR     <= STAR_WAIT;
                           end
                        end
                    end
                    end // STAR_RUN
               STAR_WAIT: begin
                    if (|(ARGRANT&ARVALID&ARREADY)) begin
                        stateAR <= STAR_RUN;
                    end
                    end // STAR_WAIT
               STAR_LOCK: begin
                    if (unlock) begin
                        locked      <= 1'b0;
                        locksid     <=  'h0;
                        argrant_reg <=  'h0;
                        stateAR     <= STAR_RUN;
                    end
                    end // STAR_LOCK
               endcase
           end
     end
     //-----------------------------------------------------------
     assign ARGRANT = (stateAR==STAR_RUN) ? priority_sel(ARSELECT&ARVALID)
                                          : argrant_reg;
     //-----------------------------------------------------------
     // write-address arbiter
     //-----------------------------------------------------------
     wire [WIDTH_SID-1:0] fifo_push_din   = (AWGRANT[0]==1'b1) ? AWSID0
                                          : (AWGRANT[1]==1'b1) ? AWSID1
                                          : (AWGRANT[2]==1'b1) ? AWSID2
                                          : (AWGRANT[3]==1'b1) ? AWSID3
                                          : (AWGRANT[4]==1'b1) ? AWSID4
                                          : (AWGRANT[5]==1'b1) ? AWSID5
                                          : (AWGRANT[6]==1'b1) ? AWSID6
                                          : (AWGRANT[7]==1'b1) ? AWSID7
                                          : 'h0;
     wire                 fifo_push_valid = |(AWGRANT&AWREADY);
     wire                 fifo_pop_ready;
     wire                 fifo_pop_valid;
     wire [WIDTH_SID-1:0] fifo_pop_dout ;
     //-----------------------------------------------------------
     reg [NUM-1:0] awgrant_reg;
     //-----------------------------------------------------------
     localparam STAW_RUN    = 'h0,
                STAW_WAIT   = 'h1,
                STAW_LOCK   = 'h2;
     reg [1:0] stateAW=STAW_RUN;
     always @ (posedge ACLK or negedge ARESETn) begin
     if (ARESETn==1'b0) begin
         awgrant_reg <=  'h0;
         unlock      <= 1'b0;
         stateAW     <= STAW_RUN;
     end else begin
     case (stateAW)
     STAW_RUN: begin
          if (~locked) begin
              if (|AWGRANT) begin
                  if (~|(AWGRANT&AWREADY)) begin
                     awgrant_reg <= AWGRANT;
                     stateAW     <= STAW_WAIT;
                  end
              end
          end else begin
              if (locksid[WIDTH_SID-1:WIDTH_ID]==MID0) begin
                 if (AWSELECT[0]&AWVALID[0]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID0[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 6'b000001;
                         if (~AWLOCK[0]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID0, MID0);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID1) begin
                 if (AWSELECT[1]&AWVALID[1]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID1[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h2;
                         if (~AWLOCK[1]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID1, MID1);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID2) begin
                 if (AWSELECT[2]&AWVALID[2]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID2[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h4;
                         if (~AWLOCK[2]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID2, MID2);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID3) begin
                 if (AWSELECT[3]&AWVALID[3]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID3[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h8;
                         if (~AWLOCK[3]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID3, MID3);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID4) begin
                 if (AWSELECT[4]&AWVALID[4]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID4[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h10;
                         if (~AWLOCK[4]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID4, MID4);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID5) begin
                 if (AWSELECT[5]&AWVALID[5]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID5[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h20;
                         if (~AWLOCK[5]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID5, MID5);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID6) begin
                 if (AWSELECT[6]&AWVALID[6]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID6[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h40;
                         if (~AWLOCK[6]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID6, MID6);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end else if (locksid[WIDTH_SID-1:WIDTH_ID]==MID7) begin
                 if (AWSELECT[7]&AWVALID[7]) begin
                     if (locksid[WIDTH_SID-1:WIDTH_ID]==
                         AWSID7[WIDTH_SID-1:WIDTH_ID]) begin
                         awgrant_reg <= 8'h80;
                         if (~AWLOCK[7]) unlock <= 1'b1;
                         else            unlock <= 1'b0;
                         stateAW <= STAW_LOCK;
                     end else begin
                         // synopsys translate_off
                         `ifdef RIGOR
                         $display("%0t %m ERROR un-expected write-request during lock AWID(0x%2x) from MID(%3d)",
                                          $time, AWSID7, MID7);
                         `endif
                         // synopsys translate_on
                     end
                 end
              end
              // synopsys translate_off
              `ifdef RIGOR
              else begin
                   $display("%0t %m ERROR un-expected MID for lock 0x%x",
                                    $time, locksid[WIDTH_SID-1:WIDTH_ID]);
              end
              `endif
              // synopsys translate_on
          end
          end // STAW_RUN
     STAW_WAIT: begin
          if (|(AWGRANT&AWVALID&AWREADY)) begin
             awgrant_reg <= 'h0;
             stateAW     <= STAW_RUN;
          end
          end // STAW_WAIT
     STAW_LOCK: begin
          if (|(AWGRANT&AWVALID&AWREADY)) begin
             awgrant_reg <=  'h0;
             unlock      <= 1'b0;
             stateAW     <= STAW_RUN;
          end
          end // STAW_LOCK
     default: begin
          awgrant_reg <=  'h0;
          unlock      <= 1'b0;
          stateAW     <= STAW_RUN;
              end
     endcase
     end // if
     end // always
     //-----------------------------------------------------------
     assign AWGRANT = ((stateAW==STAW_RUN)&~locked) ? priority_sel(AWSELECT&AWVALID)
                                                    : awgrant_reg;
     //-----------------------------------------------------------
     axi_fifo_sync #(.FDW(WIDTH_SID), .FAW(4))
     u_axi_fifo_sync
     (
           .rstn     (ARESETn)
         , .clr      (1'b0   )
         , .clk      (ACLK   )
         , .wr_rdy   (               )
         , .wr_vld   (fifo_push_valid)
         , .wr_din   (fifo_push_din  )
         , .rd_rdy   (fifo_pop_ready )
         , .rd_vld   (fifo_pop_valid )
         , .rd_dout  (fifo_pop_dout  )
         , .full     (               )
         , .empty    ()
         , .fullN    ()
         , .emptyN   ()
         , .item_cnt ()
         , .room_cnt ()
     );
     //-----------------------------------------------------------
     wire active_wvalid = |(WGRANT&WVALID);
     wire active_wready = |WREADY;
     wire active_wlast  = |(WGRANT&WLAST);
     //-----------------------------------------------------------
     assign fifo_pop_ready = fifo_pop_valid
                           & active_wvalid
                           & active_wready
                           & active_wlast;
     //-----------------------------------------------------------
     always @ ( * ) begin
     if (~fifo_pop_valid) begin
         WGRANT = 8'h0;
     end else begin
              if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID0) WGRANT = 8'h1;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID1) WGRANT = 8'h2;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID2) WGRANT = 8'h4;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID3) WGRANT = 8'h8;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID4) WGRANT = 8'h10;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID5) WGRANT = 8'h20;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID6) WGRANT = 8'h40;
         else if (fifo_pop_dout[WIDTH_SID-1:WIDTH_ID]==MID7) WGRANT = 8'h80;
         else WGRANT = 8'h0;
     end // if
     end // always
     // synopsys translate_off
     `ifdef RIGOR
     always @ (negedge ACLK or negedge ARESETn) begin
          if (ARESETn==1'b1) begin
              if (fifo_pop_valid&~|WGRANT) begin
                  $display("%0t %m ERROR FIFO valid, but none granted WGRANT", $time);
              end
          end
     end
     `endif
     // synopsys translate_on
     //-----------------------------------------------------------
     function [NUM-1:0] priority_sel;
     input    [NUM-1:0] request;
     begin
          casex (request)
          8'bxxxxxxx1: priority_sel = 8'h1;
          8'bxxxxxx10: priority_sel = 8'h2;
          8'bxxxxx100: priority_sel = 8'h4;
          8'bxxxx1000: priority_sel = 8'h8;
          8'bxxx10000: priority_sel = 8'h10;
          8'bxx100000: priority_sel = 8'h20;
          8'bx1000000: priority_sel = 8'h40;
          8'b10000000: priority_sel = 8'h80;
          default:   priority_sel = 8'h0;
          endcase
     end
     endfunction
     //-----------------------------------------------------------
endmodule
