                     | ({NUM_AW_WIDTH{AWGRANT[5]}}&bus_aw[5])
                     | ({NUM_AW_WIDTH{AWGRANT[6]}}&bus_aw[6])
                     | ({NUM_AW_WIDTH{AWGRANT[7]}}&bus_aw[7])
                     ;
            `endif
     end
     `define S_WBUS {S_WID\
                    ,S_WDATA\
                    ,S_WSTRB\
                    ,S_WLAST\
                    ,S_WVALID\
                    `ifdef AMBA_AXI_WUSER\
                    ,S_WUSER\
                    `endif\
                    }
     always @ ( WGRANT, bus_w[0], bus_w[1], bus_w[2], bus_w[3], bus_w[4], bus_w[5], bus_w[6], bus_w[7] ) begin
            `ifdef AMBA_AXI_MUX
            case (WGRANT)
            8'h1:  `S_WBUS = bus_w[0];
            8'h2:  `S_WBUS = bus_w[1];
            8'h4:  `S_WBUS = bus_w[2];
            8'h8:  `S_WBUS = bus_w[3];
            8'h10:  `S_WBUS = bus_w[4];
            8'h20:  `S_WBUS = bus_w[5];
            8'h40:  `S_WBUS = bus_w[6];
            8'h80:  `S_WBUS = bus_w[7];
            default:    `S_WBUS = 'h0;
            endcase
            `else
            `S_WBUS = ({NUM_W_WIDTH{WGRANT[0]}}&bus_w[0])
                    | ({NUM_W_WIDTH{WGRANT[1]}}&bus_w[1])
                    | ({NUM_W_WIDTH{WGRANT[2]}}&bus_w[2])
                    | ({NUM_W_WIDTH{WGRANT[3]}}&bus_w[3])
                    | ({NUM_W_WIDTH{WGRANT[4]}}&bus_w[4])
                    | ({NUM_W_WIDTH{WGRANT[5]}}&bus_w[5])
                    | ({NUM_W_WIDTH{WGRANT[6]}}&bus_w[6])
                    | ({NUM_W_WIDTH{WGRANT[7]}}&bus_w[7])
                    ;
            `endif
     end
     `define S_ARBUS {S_ARID\
                     ,S_ARADDR\
                     ,S_ARLEN\
                     ,S_ARLOCK\
                     ,S_ARSIZE\
                     ,S_ARBURST\
                     `ifdef AMBA_AXI_CACHE\
                     ,S_ARCACHE\
                     `endif\
                     `ifdef AMBA_AXI_PROT\
                     ,S_ARPROT\
                     `endif\
                     ,S_ARVALID\
                     `ifdef AMBA_QOS\
                     ,S_ARQOS\
                     ,S_ARREGION\
                     `endif\
                     `ifdef AMBA_AXI_ARUSER\
                     ,S_ARUSER\
                     `endif\
                     }
     always @ ( ARGRANT, bus_ar[0], bus_ar[1], bus_ar[2], bus_ar[3], bus_ar[4], bus_ar[5], bus_ar[6], bus_ar[7] ) begin
            `ifdef AMBA_AXI_MUX
            case (ARGRANT)
            8'h1:  `S_ARBUS = bus_ar[0];
            8'h2:  `S_ARBUS = bus_ar[1];
            8'h4:  `S_ARBUS = bus_ar[2];
            8'h8:  `S_ARBUS = bus_ar[3];
            8'h10:  `S_ARBUS = bus_ar[4];
            8'h20:  `S_ARBUS = bus_ar[5];
            8'h40:  `S_ARBUS = bus_ar[6];
            8'h80:  `S_ARBUS = bus_ar[7];
            default:    `S_ARBUS = 'h0;
            endcase
            `else
            `S_ARBUS = ({NUM_AR_WIDTH{ARGRANT[0]}}&bus_ar[0])
                     | ({NUM_AR_WIDTH{ARGRANT[1]}}&bus_ar[1])
                     | ({NUM_AR_WIDTH{ARGRANT[2]}}&bus_ar[2])
                     | ({NUM_AR_WIDTH{ARGRANT[3]}}&bus_ar[3])
                     | ({NUM_AR_WIDTH{ARGRANT[4]}}&bus_ar[4])
                     | ({NUM_AR_WIDTH{ARGRANT[5]}}&bus_ar[5])
                     | ({NUM_AR_WIDTH{ARGRANT[6]}}&bus_ar[6])
                     | ({NUM_AR_WIDTH{ARGRANT[7]}}&bus_ar[7])
                     ;
            `endif
     end
     //-----------------------------------------------------------
endmodule
